import React, { useState, useEffect } from "react";
import Carousel from "react-elastic-carousel";
import "./Slider.css";
const axios = require("axios");

// const Cards = [
//     {
//         img: 'https://akm-img-a-in.tosshub.com/indiatoday/images/story/202112/Tesla_Cyberquad.png?4eAWdNQcIcS9hPTGK.wDnRRuWL.7F9XF&size=770:433',
//         title: 'December 2, 2021 20:55 IST',
//         author: 'Tesla launches Cyberquad, an all-electric quad bike for kids, at about Rs 1.42 lakh'
//     },
//     {
//         img: 'https://akm-img-a-in.tosshub.com/indiatoday/images/story/202112/kejriwal-channi.jpg?fohZhyphwcUymEwl1Snip7Qgiq57VZhn&size=770:433',
//         title: 'December 2, 2021 17:14 IST',
//         author: 'Passengers push airplane off runway after tyre burst at Nepal airport. Viral video'
//     },
//     {
//         img: 'https://akm-img-a-in.tosshub.com/indiatoday/images/story/202112/Katrina_kaif_vicky_kaushal_ind_2.jpg?W0Ok28vjP8Ldd60QF2hgbKDIFb81Kw2x&size=770:433',
//         title: 'No name tags, just codes for Vicky Kaushal, Katrina Kaif wedding guests: Exclusive',
//         author: 'December 2, 2021 20:31 IST'
//     },
//     {
//         img: 'https://akm-img-a-in.tosshub.com/indiatoday/images/story/202112/passengers1_1200x768.jpeg?JYBwwtjoAJqDXkMoXSg5xPB3j.QNa_aM&size=770:433',
//         title: 'December 2, 2021 20:58 IST' ,
//         author: 'Maharashtra revises quarantine norms, RT-PCR mandatory for passengers arriving from 3 high-risk countries Maharashtra revises quarantine norms, RT-PCR mandatory for passengers arriving from 3 high-risk countries '
//     },
// ];

const Slider = () => {
  const [Cards, setCards] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const res = await axios.get("https://newsapi.org/v2/top-headlines", {
          params: {
            country: "in",
            category: "general",
            // apiKey: "b186e59534794e9a9b732580246cf18a",
            apiKey: "9ad6a21779da47c28dde78964e668571",
            apiKey: "8b08468bd2174e088385c41a3930dc08",
            sortBy: "popularity",
          },
        });
        setCards(res.data.articles.slice(0, 5));
      } catch (err) {
        console.log(err);
      }
    }
    fetchData();
  }, []);
  return (
    <div className="outer-div">
      <Carousel infiniteLoop useKeyboardArrows autoPlay>
        {Cards.map((card) => {
          const { urlToImage, title, author } = card;
          return (
            <div className="Card">
              <div className="gradient">
                <img alt="" src={urlToImage} />
              </div>
              <div className="first-txt">
                <h2>{title}</h2>
                <h4>{author}</h4>
              </div>
            </div>
          );
        })}
      </Carousel>
    </div>
  );
};

export default Slider;
