import React, { useState, useContext, useEffect } from "react";
import { GiCardDiscard } from "react-icons/gi";
import Navbar from "../Components/Navbar/Navbar";
import { NewsContext } from "../NewsContext";
import { Link, useParams } from "react-router-dom";

const ArticleOpen = () => {
  const params = useParams();
  const [generalCards, setgeneralCards] = useContext(NewsContext);
  const [sportCards, setsportsCards] = useContext(NewsContext);
  const [techCards, settechCards] = useContext(NewsContext);
  const [buisnessCards, setbuisnessCards] = useContext(NewsContext);
  const [healthCards, sethealthCards] = useContext(NewsContext);
  const [entertainCards, setentertainCards] = useContext(NewsContext);
  const [Cards, setCards] = useContext(NewsContext);
  //   console.log(params.categoryName);
  console.log(params.categoryName);

  const showdata = ({ category }) => {
    if (category === "general") {
      return (
        <div className="container wrapper">
          <img
            className="responsiv-img"
            alt=""
            src={generalCards[params.index].img}
          ></img>
          <h1 className="title">{generalCards[params.index].title}</h1>
          <div className="meta-info">
            <p>
              The Vikings needed a last-second stop to secure their sixth win of
              the season
            </p>
          </div>
          <h1 className="date-right">{generalCards[params.index].author}</h1>

          <p className="entry-content">
            he Vikings nearly delivered a knockout before having to hold off a
            furious comeback attempt by the visiting Steelers on Thursday night.
            Down 29-0, the Steelers scored 28 of the game's next 35 points…{" "}
            <Link
              to="https://www.cbssports.com/nfl/news/vikings-vs-steelers-score-live-updates-results-game-stats-highlights-for-thursday-night-football/live/"
              className="url"
            >
              READ FULL ARTICLE
            </Link>
          </p>

          <h3 className="date">{generalCards[params.index].author}</h3>
          <h3 className="author">Bryan DeArdo</h3>
        </div>
      );
    }
    if (category === "sports") {
      return (
        <div className="container wrapper">
          <img
            className="responsiv-img"
            alt=""
            src={sportCards[params.index].img}
          ></img>
          <h1 className="title">{sportCards[params.index].title}</h1>
          <div className="meta-info">
            <p>
              The Vikings needed a last-second stop to secure their sixth win of
              the season
            </p>
          </div>
          <h1 className="date-right">{sportCards[params.index].author}</h1>

          <p className="entry-content">
            he Vikings nearly delivered a knockout before having to hold off a
            furious comeback attempt by the visiting Steelers on Thursday night.
            Down 29-0, the Steelers scored 28 of the game's next 35 points…{" "}
            <Link
              to="https://www.cbssports.com/nfl/news/vikings-vs-steelers-score-live-updates-results-game-stats-highlights-for-thursday-night-football/live/"
              className="url"
            >
              READ FULL ARTICLE
            </Link>
          </p>

          <h3 className="date">{sportCards[params.index].author}</h3>
          <h3 className="author">Bryan DeArdo</h3>
        </div>
      );
    }
  };

  return (
    <div>
      <Navbar />
      {showdata(params.categoryName)}
    </div>
  );
};

export default ArticleOpen;
