import axios from "axios";
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";
import Footer from "../Footer/Footer";
import Navbar from "../Navbar/Navbar";
import "../News-Category/NewsCategory.css";

const Query = () => {
  const params = useParams();
  const [result, setResult] = useState([]);

  console.log(params.queryName);
  useEffect(() => {
    async function fetchData() {
      try {
        const res = await axios.get(
          `https://newsapi.org/v2/everything?q=${params.queryName}&apiKey=b186e59534794e9a9b732580246cf18a&sortBy=popularity`
        );
        // apiKey: "b186e59534794e9a9b732580246cf18a",
        // apiKey: "9ad6a21779da47c28dde78964e668571",

        if (res) {
          // console.log(res.data.articles);
          setResult(res.data.articles);
          console.log(result);
        }
      } catch (err) {
        console.log(err);
      }
    }
    fetchData();
  }, [params.queryName]);

  return (
    <div>
      <Navbar />
      <div>
        <div className="container">
          <h1 className="title-heading">Search Result</h1>
        </div>
        <div className="band">
          {result.map((cardd) => {
            const { urlToImage, title, author } = cardd;
            return (
              <div className="item">
                <Link
                  to="https://design.tutsplus.com/articles/international-artist-feature-malaysia--cms-26852"
                  className="card"
                >
                  <div
                    className="thumb"
                    style={{ backgroundImage: "url(" + urlToImage + ")" }}
                  ></div>
                  <article>
                    <h1>{title}</h1>
                    <span>{author}</span>
                  </article>
                </Link>
              </div>
            );
          })}
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Query;
