import React, { useState, createContext, useEffect } from "react";
import ArticlePage from "./Layout/ArticlePage";
const axios = require("axios");

export const NewsContext = createContext();

export const NewsProvider = (props) => {
  const [generalCards, setgeneralCards] = useState([]);
  const [sportCards, setsportsCards] = useState([]);
  const [techCards, settechCards] = useState([]);
  const [buisnessCards, setbuisnessCards] = useState([]);
  const [healthCards, sethealthCards] = useState([]);
  const [entertainCards, setentertainCards] = useState([]);
  useEffect(() => {
    async function fetchgeneralData() {
      try {
        const res = await axios.get("https://newsapi.org/v2/top-headlines", {
          params: {
            country: "in",
            category: "general",
            // apiKey: "b186e59534794e9a9b732580246cf18a",
            apiKey: "9ad6a21779da47c28dde78964e668571",
            // apikey: "94c83c96e50d4fec839229c7fbabfb30",
            // apikey: "8b08468bd2174e088385c41a3930dc08",
            sortBy: "popularity",
          },
        });

        // console.log(res.data.articles);

        setgeneralCards(res.data.articles.slice(0, 18));
        console.log(Cards);
      } catch (err) {
        console.log(err);
      }
    }
    fetchgeneralData();

    async function fetchsportsData() {
      try {
        const res = await axios.get("https://newsapi.org/v2/top-headlines", {
          params: {
            country: "in",
            category: "general",
            // apiKey: "b186e59534794e9a9b732580246cf18a",
            apiKey: "9ad6a21779da47c28dde78964e668571",
            // apikey: "94c83c96e50d4fec839229c7fbabfb30",
            // apikey: "8b08468bd2174e088385c41a3930dc08",
            sortBy: "popularity",
          },
        });

        // console.log(res.data.articles);

        setsportsCards(res.data.articles.slice(0, 18));
      } catch (err) {
        console.log(err);
      }
    }
    fetchsportsData();

    async function fetchbuisnessData() {
      try {
        const res = await axios.get("https://newsapi.org/v2/top-headlines", {
          params: {
            country: "in",
            category: "general",
            // apiKey: "b186e59534794e9a9b732580246cf18a",
            apiKey: "9ad6a21779da47c28dde78964e668571",
            // apikey: "94c83c96e50d4fec839229c7fbabfb30",
            // apikey: "8b08468bd2174e088385c41a3930dc08",
            sortBy: "popularity",
          },
        });

        // console.log(res.data.articles);

        setbuisnessCards(res.data.articles.slice(0, 18));
      } catch (err) {
        console.log(err);
      }
    }
    fetchbuisnessData();

    async function fetchtechData() {
      try {
        const res = await axios.get("https://newsapi.org/v2/top-headlines", {
          params: {
            country: "in",
            category: "general",
            // apiKey: "b186e59534794e9a9b732580246cf18a",
            apiKey: "9ad6a21779da47c28dde78964e668571",
            // apikey: "94c83c96e50d4fec839229c7fbabfb30",
            // apikey: "8b08468bd2174e088385c41a3930dc08",
            sortBy: "popularity",
          },
        });

        // console.log(res.data.articles);

        settechCards(res.data.articles.slice(0, 18));
      } catch (err) {
        console.log(err);
      }
    }
    fetchtechData();

    async function fetchentertainData() {
      try {
        const res = await axios.get("https://newsapi.org/v2/top-headlines", {
          params: {
            country: "in",
            category: "general",
            // apiKey: "b186e59534794e9a9b732580246cf18a",
            apiKey: "9ad6a21779da47c28dde78964e668571",
            // apikey: "94c83c96e50d4fec839229c7fbabfb30",
            // apikey: "8b08468bd2174e088385c41a3930dc08",
            sortBy: "popularity",
          },
        });

        setentertainCards(res.data.articles.slice(0, 18));
      } catch (err) {
        console.log(err);
      }
    }
    fetchentertainData();

    async function fetchhealthData() {
      try {
        const res = await axios.get("https://newsapi.org/v2/top-headlines", {
          params: {
            country: "in",
            category: "general",
            // apiKey: "b186e59534794e9a9b732580246cf18a",
            apiKey: "9ad6a21779da47c28dde78964e668571",
            // apikey: "94c83c96e50d4fec839229c7fbabfb30",
            // apikey: "8b08468bd2174e088385c41a3930dc08",
            sortBy: "popularity",
          },
        });

        // console.log(res.data.articles);

        sethealthCards(res.data.articles.slice(0, 18));
      } catch (err) {
        console.log(err);
      }
    }
    fetchhealthData();
  }, []);

  return (
    <NewsContext.Provider value={[Cards, setCards]}>
      {props.children}
      {/* {console.log(Cards)} */}
      {/* <ArticlePage /> */}
    </NewsContext.Provider>
  );
};
