import axios from "axios";
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";

const Cards = [
  {
    img: "https://cdn.cnn.com/cnnnext/dam/assets/170714082227-01-tom-holland-zendaya-dating-super-tease.jpg",
    title: "Tom Holland and Zendaya don't want to talk about it",
    author: "2021-11-18T18:46:33Z",
  },
  {
    img: "https://i.guim.co.uk/img/media/07d4a642faba8ad2d7d0caa37de07d9e9aff75bd/0_0_1000_600/master/1000.png?width=1200&height=630&quality=85&auto=format&fit=crop&overlay-align=bottom%2Cleft&overlay-width=100p&overlay-base64=L2ltZy9zdGF0aWMvb3ZlcmxheXMvdGctZGVmYXVsdC5wbmc&enable=upscale&s=e7615c916e0145c559737e623adfcc13",
    title:
      "Sign up for Pushing Buttons: Keza MacDonald’s weekly look at the world of gaming",
    author: " 2021-11-24T16:02:35Z",
  },
  {
    img: "https://images2.markets.businessinsider.com/61a790355d47cc0018e8c631?format=jpeg",
    title:
      "AMC's Spiderman NFT helped generate the movie chain's second best one-day sales for 'No Way Home' premiere, CEO Aron says",
    author: "2021-12-01T16:21:39Z",
  },
  {
    img: "https://i.guim.co.uk/img/media/9b7412a06451584d8e594c9d08b3cd0623d23d02/0_32_1800_1081/master/1800.jpg?width=1200&height=630&quality=85&auto=format&fit=crop&overlay-align=bottom%2Cleft&overlay-width=100p&overlay-base64=L2ltZy9zdGF0aWMvb3ZlcmxheXMvdGctb3BpbmlvbnMucG5n&enable=upscale&s=3aaa1cab625fd350260b2ba3ec49a62b",
    title:
      "No dramas: superhero movies and their opening weekend cash cows crowd out realistic films | Greg Jericho",
    author: "2021-11-22T16:30:31Z",
  },
  {
    img: "https://i.insider.com/6197fc1d1ca92500180bffd5?width=1200&format=jpeg",
    title:
      "Mississippi banned most abortions to be the 'safest state' for the unborn. Meanwhile, one in three Mississippi kids live in poverty",
    author: "2021-11-26T14:23:25Z",
  },
  {
    img: "https://cdn.vox-cdn.com/thumbor/XdaUUdXf5eIAPHfLu4z5s8CyTwA=/47x0:2240x1148/fit-in/1200x630/cdn.vox-cdn.com/uploads/chorus_asset/file/23041702/steve_rogers_musical_hawkeye.jpg",
    title:
      "Listen to Hawkeye’s Rogers: The Musical song, and how Marvel pulled it off",
    author: "2021-11-25T15:44:00Z",
  },
  {
    img: "https://img-cdn.tnwcdn.com/image/neural?filter_last=1&fit=1280%2C640&url=https%3A%2F%2Fcdn0.tnwcdn.com%2Fwp-content%2Fblogs.dir%2F1%2Ffiles%2F2021%2F11%2Frainbow_news.jpg&signature=226dcdbc25aa0f59c20b1668eff081d3",
    title:
      "Google News probably thinks I cover Spiderman because algorithms are dumb",
    author: "2021-11-18T19:20:27Z",
  },
];

const Query = () => {
  const params = useParams();
  const [result, setResult] = useState(["spiderman"]);
  console.log(params);
  useEffect(() => {
    async function fetchData() {
      try {
        const res = await axios.get("https://newsapi.org/v2/everything", {
          params: {
            q: params.queryName,
            // apiKey: "b186e59534794e9a9b732580246cf18a",
            apiKey: "9ad6a21779da47c28dde78964e668571",
            sortBy: "popularity",
          },
        });
        setResult(res.data.articles);

        console.log(result);
      } catch (err) {
        console.log(err);
      }
    }
    fetchData();
  }, [params.queryName]);

  return (
    <div>
      <div>
        <div className="container">
          <h1 className="title-heading">Search Result</h1>
        </div>
        <div className="band">
          {Cards.map((cardd) => {
            const { urlToImage, title, author } = cardd;
            return (
              <div className="item">
                <Link
                  to="https://design.tutsplus.com/articles/international-artist-feature-malaysia--cms-26852"
                  className="card"
                >
                  <div
                    className="thumb"
                    style={{ backgroundImage: "url(" + urlToImage + ")" }}
                  ></div>
                  <article>
                    <h1>{title}</h1>
                    <span>{author}</span>
                  </article>
                </Link>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default Query;
